import { useState } from "react";

export default function Todo({
    text,
    isCompleted
}) {
    const [isCompletedValue, setComplete] = useState(isCompleted);

    return (
        <tr className="todo">
            <td>{text}</td>
            <td>{isCompletedValue ? 'Done' : 'In Progress'}</td>
            <td className="todo-action">
                <button className="btn todo-btn" onClick={(oldValue => !oldValue)}>Change status</button>
            </td>
        </tr>
    );
}
