import './App.css';
import { useState } from 'react';

function App() {
  const [form, setForm] = useState({
    firstName: '',
    lastName: '',
    age: ''
  });

  const onChange = (e) => {
    setForm(state => ({ ...state, [e.target.name]: e.target.value }))
  }

  return (
    <div className="App">
      <form>
        <div>
          <label htmlFor='firstName' style={{ display: "block" }}>First name</label>
          <input
            id="firstName"
            name="firstName"
            value={form.firstName}
            onChange={onChange} />
        </div>
        <div>
          <label htmlFor='lastName' style={{ display: "block" }}>Last name</label>
          <input
            id="lastName"
            name="lastName"
            value={form.lastName}
            onChange={onChange} />
        </div>
        <div>
          <label htmlFor='age' style={{ display: "block" }}>Last name</label>
          <input
            id="age"
            name="age"
            value={Number.parseInt(form.age)}
            onChange={onChange} />
        </div>
      </form>
    </div>
  );
}

export default App;
