import './App.css';
import { useState } from 'react';

function App() {
  const [form, setForm] = useState({
    firstName: '',
    lastName: '',
  });

  const [firstName, setFirstName] = useState('Pesho');
  const [lastName, setLastName] = useState('G-to');

  const onChange = (e) => {
    console.log(e.target.name);
    console.log(e.target.value);

    setForm((state) => state[e.target.name] = e.target.value)
  }

  return (
    <div className="App">
      <form>
        <div>
          <label htmlFor='firstName' style={{ display: "block" }}>First name</label>
          <input
            id="firstName"
            name="firstName"
            value={firstName}
            onChange={onChange} />
        </div>
        <div>
          <label htmlFor='lastName' style={{ display: "block" }}>Last name</label>
          <input
            id="lastName"
            name="lastName"
            value={lastName}
            onChange={onChange} />
        </div>
      </form>
    </div>
  );
}

export default App;
