import './App.css';
import { useState } from 'react';

function App() {
    const [form, setForm] = useState({
        firstName: '',
        lastName: '',
        age: '',
        select: ''
    });

    const onChange = (e) => {
        console.log(form);
        setForm(state => ({ ...state, [e.target.name]: e.target.value }))
    }

    return (
        <div className="App">
            <form>
                <div>
                    <label htmlFor='firstName' style={{ display: "block" }}>First name</label>
                    <input
                        id="firstName"
                        name="firstName"
                        value={form.firstName}
                        onChange={onChange} />
                </div>
                <div>
                    <label htmlFor='lastName' style={{ display: "block" }}>Last name</label>
                    <input
                        id="lastName"
                        name="lastName"
                        value={form.lastName}
                        onChange={onChange} />
                </div>
                <div>
                    <label htmlFor='age' style={{ display: "block" }}>Age</label>
                    <input
                        id="age"
                        name="age"
                        type="number"
                        value={Number(form.age)}
                        onChange={onChange} />
                </div>
                <div>
                    <label htmlFor='select' style={{ display: "block" }}>Select</label>
                    <select
                        id="select"
                        name="select"
                        value={form.select}
                        onChange={onChange}>
                        <option value='selected 1'>1</option>
                        <option value='selected 2'>2</option>
                        <option value='selected 3'>3</option>
                    </select>
                </div>
            </form>
        </div>
    );
}

export default App;
