import './App.css';
import { useState } from 'react';

function App() {
  const [form, setForm] = useState({
    firstName: '',
    lastName: ''
  });

  const onChange = (e) => {
    // setForm((state) => )
    console.log(form);

    setForm(state => state[e.target.name] = e.target.value)
  }

  return (
    <div className="App">
      <form>
        <div>
          <label htmlFor='firstName' style={{ display: "block" }}>First name</label>
          <input
            id="firstName"
            name="firstName"
            value={form.firstName}
            onChange={onChange} />
        </div>
        <div>
          <label htmlFor='lastName' style={{ display: "block" }}>Last name</label>
          <input
            id="lastName"
            name="lastName"
            value={form.lastName}
            onChange={onChange} />
        </div>
      </form>
    </div>
  );
}

export default App;
