import './App.css';
import { useState } from 'react';

function App() {
    const [form, setForm] = useState({
        firstName: '',
        lastName: '',
        age: ''
    });

    const onChange = (e) => {
        setForm(state => ({ ...state, [e.target.name]: e.target.value }))
    }

    return (
        <div className="App">
            <form>
                <div>
                    <label htmlFor='firstName' style={{ display: "block" }}>First name</label>
                    <input
                        id="firstName"
                        name="firstName"
                        value={form.firstName}
                        onChange={onChange} />
                </div>
                <div>
                    <label htmlFor='lastName' style={{ display: "block" }}>Last name</label>
                    <input
                        id="lastName"
                        name="lastName"
                        value={form.lastName}
                        onChange={onChange} />
                </div>
                <div>
                    <label htmlFor='age' style={{ display: "block" }}>Age</label>
                    <input
                        id="age"
                        name="age"
                        type="number"
                        value={Number(form.age)}
                        onChange={onChange} />
                </div>
                <div>
                    <label htmlFor='age' style={{ display: "block" }}>Select</label>
                    <select>
                        <option>1</option>
                        <option>2</option>
                        <option>3</option>
                    </select>
                </div>
            </form>
        </div>
    );
}

export default App;
