import { useEffect } from "react";
import { useParams } from "react-router-dom";
const baseUrl = 'https://swapi.dev/api/people'

export default function Person({
    name
}) {
    const { id } = useParams();
    useEffect(() => {
        fetch(`${baseUrl}/${id}`)
            .then(res => res.json())
            .then(data => setPeople(data.results))
    }, [])

    return (
        <>
            <h3>{name}</h3>
            <h5>{id}</h5>
        </>
    );
}
