import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
const baseUrl = 'https://swapi.dev/api/people'

export default function Person({
}) {
    const [person, setPerson] = useState({});
    const { id } = useParams();

    useEffect(() => {
        fetch(`${baseUrl}/${id}`)
            .then(res => res.json())
            .then(data => setPerson(data))
    }, [])

    return (
        <>
            <h3>{person.name}</h3>
            <h6>{person.height}</h6>
            <h6>{person.mass}</h6>
        </>
    );
}
